mapper.update:
UPDATE noticeBoard
SET title = #{title}, content = #{content}
WHERE notice_id = #{notice_id}

mapper.select:
SELECT *
FROM (SELECT nr.reply_id, nr.board_id, m.login_id, nr.content, nr.regdate
      FROM noticeReply nr, member m
      WHERE n.writer_id = m.id) A
WHERE notice_id= #{notice_id}
ORDER BY reply_id;

mapper.update:
UPDATE noticeReply
SET content = #{content}
WHERE reply_id = #{reply_id}
